//get items from axios
import axios from "axios";
import { useEffect, useState } from "react";
type ItemType = {
    id: String;
    name: String;
    description: String;
    price: number;
    image: String;
};
const fetch = "http://localhost:8080/api/menu/getAllMenu";
export default function getItems() {
    const [items, setItems] = useState<ItemType[]>([]);
    useEffect(() => {
        axios.get(fetch).then((res) => {
            setItems(res.data);
        });
    }, []);
    return items;
}

