//get items from axios
import axios from "axios";
import { useEffect, useState } from "react";
type ItemType = {
    description: string;
    image: string;
    ItemId: string;
    name: string;
    price: number;
};
const fetch = "http://localhost:8080/api/menu/";
export default function GetItems() {
    const [items, setItems] = useState<ItemType[]>([]);
    const fetchData = async () => {
        const { data } = await axios.get(fetch);
        console.log(data);
        setItems(data);
    };
    useEffect(() => {
        fetchData();
    }, []);
    return (
        <div className="p-10 ">
            <div className="w-full lg:max-w-full lg:flex w-20">
                {items.map((item) => (
                    <div key={item.ItemId} className="p-4 ">
                        <div className="c-card block  shadow-md hover:shadow-xl rounded-lg overflow-hidden">
                            <div className=" bg-gray-50 p-2">
                                <img
                                    src={item.image}
                                    alt="item"
                                    className="object-contain"
                                />
                            </div>
                            <h1 className="subpixel-antialiased text-xl font-extrabold tracking-wide text-center bg-grey  bg-blue-500 p-1">
                                {item.name}
                            </h1>
                            <p className="subpixel-antialiased text-sm">
                                {item.description}
                            </p>
                            <p className="subpixel-antialiased text-base font-extrabold tracking-wide text-center bg-grey  bg-purple-400 p-1">
                                {item.price} Eth
                            </p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
